import React, { useState, useEffect } from 'react';
import Request from '../Actions/Request';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


function Form(props) {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [repassword, setRePassword] = useState("");
    const [type, setType] = useState("signup");
    const [message, setMessage] = useState("");

    const navigate = useNavigate();
    useEffect(() => {
        setType(props.type);
    }, [props.type]);

    async function Authenticate() {
        if (type === 'signup') {
            let data = { name, email, password, repassword, type };
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            };
            const body = JSON.stringify(data);

            const res = await axios.post(Request.auth, body, config);
            if (res.data.status !== 200) {
                setMessage(res.data.error);
            } else {
                navigate("/signin");
            }
        }
        else if (type === 'signin') {
            let data = { email, password, type };
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            };
            const body = JSON.stringify(data);

            const res = await axios.post(Request.auth, body, config);

            if (res.data.status !== 200) {
                setMessage(res.data.error);
            } else {
                localStorage.setItem('isAutenticated', true);
                localStorage.setItem('user', res.data.name);
                localStorage.setItem('userEmail', res.data.username);
                navigate("/dashboard");
            }
        }
    }


    const signupForm = (title) => {
        return (
            <div className='container mt-5'>
                <div className='card'>
                    <h4 className='heading p-2'>{title}</h4>
                    <div className='card-body'>
                        <div class="mb-3 row">
                            <label class="col-sm-2 col-form-label">Name</label>
                            <div class="col-sm-10">
                                <input type="text" class="form-control" value={name} onChange={(e) => { setName(e.target.value) }} name="name" />
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label class="col-sm-2 col-form-label">Email</label>
                            <div class="col-sm-10">
                                <input type="email" class="form-control" value={email} name="email" onChange={(e) => { setEmail(e.target.value) }} />
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label class="col-sm-2 col-form-label">Password</label>
                            <div class="col-sm-10">
                                <input type="password" class="form-control" value={password} name="password" onChange={(e) => { setPassword(e.target.value) }} />
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label class="col-sm-2 col-form-label">Confirm Password</label>
                            <div class="col-sm-10">
                                <input type="password" class="form-control" value={repassword} name="repassword" onChange={(e) => { setRePassword(e.target.value) }} />
                            </div>
                        </div>
                        <div className='conatiner'>
                            <button className='btn btn-outline btn-info' onClick={Authenticate}>{title}</button>
                            <p className='bold'>{message}</p>
                        </div>


                    </div>
                </div>

            </div>
        );
    }

    const signinForm = (title) => {
        return (
            <div className='container mt-5'>
                <div className='card'>
                    <h4 className='heading p-2'>{title}</h4>
                    <div className='card-body'>

                        <div class="mb-3 row">
                            <label class="col-sm-2 col-form-label">Email</label>
                            <div class="col-sm-10">
                                <input type="email" class="form-control" value={email} name="email" onChange={(e) => { setEmail(e.target.value) }} />
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label class="col-sm-2 col-form-label">Password</label>
                            <div class="col-sm-10">
                                <input type="password" class="form-control" value={password} name="password" onChange={(e) => { setPassword(e.target.value) }} />
                            </div>
                        </div>
                        <div className='conatiner'>
                            <button className='btn btn-outline btn-warning' onClick={Authenticate}>{title}</button>
                            <p className='bold'>{message}</p>
                        </div>


                    </div>
                </div>

            </div>
        );

    }



    if (props.type === 'signup') {
        return signupForm(props.title)
    }
    else if (props.type === 'signin') {
        return signinForm(props.title)
    }
}

export default Form;
