import React, { useState } from 'react';
import Navbar from './Navbar';
import axios from 'axios';
import Request from '../Actions/Request';
import jsPDF from "jspdf";
import "jspdf-autotable";

function Reports() {
    const [summaryReport, setSummaryReport] = useState([]);
    const [detailedReport, setDetailedReport] = useState([]);
    const [currentFilterDate, setCurrentFilterDate] = useState("");

    async function getFilterData() {
        const data = {
            user: localStorage.getItem('userEmail'),
            filterDate: currentFilterDate
        }
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        };
        const body = JSON.stringify(data);
        const res = await axios.post(Request.filterAppointment, body, config);
        if (res.data.status === 200) {
            setDetailedReport(res.data.appointments)
            let uniqueDates = []
            for (let index = 0; index < res.data.appointments.length; index++) {
                if (uniqueDates.includes(res.data.appointments[index].appointment_date)) {

                } else {
                    uniqueDates.push(res.data.appointments[index].appointment_date);
                }
            }
            let tempReport = []
            for (let index = 0; index < uniqueDates.length; index++) {
                let total = 0;
                let closed = 0;
                let canceled = 0;
                for (let index1 = 0; index1 < res.data.appointments.length; index1++) {
                    if (res.data.appointments[index1].appointment_date === uniqueDates[index]) {
                        total++;
                        if (res.data.appointments[index1].appointment_status === "Closed") {
                            closed++;
                        }
                        if (res.data.appointments[index1].appointment_status === "Canceled") {
                            canceled++;
                        }

                    }
                }
                let temp = {
                    date: uniqueDates[index],
                    total: total,
                    closed: closed,
                    canceled: canceled,
                }
                tempReport.push(temp)
            }
            setSummaryReport(tempReport);
        }
        else {
            alert(res.data.error);
        }
    }

    function generatePDF() {
        const unit = "pt";
        const size = "A4"; // Use A1, A2, A3 or A4
        const orientation = "portrait"; // portrait or landscape

        const marginLeft = 40;
        const doc = new jsPDF(orientation, unit, size);

        doc.setFontSize(15);

        let title = "Detailed Report";
        let headers = [["Date", "Patient Name", "Status"]];

        let data = detailedReport.map(elt => [elt.appointment_date, elt.patient_name, elt.appointment_status]);

        let content = {
            startY: 50,
            head: headers,
            body: data
        };

        doc.text(title, marginLeft, 40);
        doc.autoTable(content);
        doc.save("Detailed_report.pdf")

        let stitle = "Summary Report";
        let sheaders = [["Date", "# Appointments", "# Closed", "# Canceled"]];

        let sdata = summaryReport.map(elt => [elt.date, elt.total, elt.closed, elt.canceled]);

        let scontent = {
            startY: 50,
            head: sheaders,
            body: sdata
        };

        doc.text(stitle, marginLeft, 40);
        doc.autoTable(scontent);
        doc.save("Summary_report.pdf")
    }

    return (
        <div>
            <Navbar />
            <div className='container mt-5'>

                <div className='row'>
                    <div className='col-3'>
                        <input type="month" className='form-control' value={currentFilterDate} onChange={(e) => setCurrentFilterDate(e.target.value)} />
                    </div>
                    <div className='col-3'>
                        <button className='btn btn-outline-warning' onClick={getFilterData}>Filter</button>
                    </div>
                    <div className='col-3'>
                        <button className='btn btn-outline-primary' onClick={generatePDF}>Export Report</button>
                    </div>
                </div>

                <div className='row mt-5'>
                    <h5>Summary Report</h5>
                    <table class="table">

                        <thead>
                            <tr>
                                <th scope="col">Date</th>
                                <th scope="col"># Appointments</th>
                                <th scope="col"># Closed</th>
                                <th scope="col"># Canceled</th>
                            </tr>
                        </thead>
                        <tbody>
                            {summaryReport.map((sreport) => {
                                return (
                                    <tr>
                                        <th scope="row">{sreport.date}</th>
                                        <td>{sreport.total}</td>
                                        <td>{sreport.closed}</td>
                                        <td>{sreport.canceled}</td>
                                    </tr>
                                );
                            })}


                        </tbody>
                    </table>
                </div>

            </div>

            <div className='container mt-5'>
                <h5>Detailed Report</h5>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col">Date</th>
                            <th scope="col">Patient Name</th>
                            <th scope="col">Status</th>

                        </tr>
                    </thead>
                    <tbody>
                        {detailedReport.map((dreport) => {
                            return (
                                <tr>
                                    <th scope="row">{dreport.appointment_date}</th>
                                    <td>{dreport.patient_name}</td>
                                    <td>{dreport.appointment_status}</td>

                                </tr>
                            );
                        })}


                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Reports;
